import React from 'react';
import "./Project.css";
import Zoom from 'react-reveal/Zoom';
import { Container, Col } from 'react-bootstrap';


function Projects(props) {
    const projects = props.data;
    console.log(projects)
   
    return (
        <div>
            <Zoom left>
            <h1 className="project-head">Have a look at some of the projects which I created</h1>
            {projects.map((project)=>{
                const previewAvailable = project.previewUrl
                return(
                    <Container>
                        <div className="cards-list">
                            <div className="card" style={{backgroundImage: `url(${project.technology})`}}>
                                <div className="content">
                                    <Col className="project-head">{project.head}</Col>
                                    <Col className="project-desc">{project.description}</Col>
                                    <b>{previewAvailable ? <Col className="project-redirect-button">
                                        <a className="btnMail" href={project.previewUrl}>Preview Code</a>
                                    </Col> : 'Preview Not Available'}</b>
                                    
                                </div>
                            </div>
                        </div>
                    </Container>
                );
            })}
            </Zoom>
        </div>
        );
}

export default Projects;
