import React, { Suspense } from 'react';
import './App.css';
import Loader from './component/Loader/Loader.js';
import { Route, Link, Switch, BrowserRouter as Router } from 'react-router-dom';
import { Nav, Navbar } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
const Contact = React.lazy(() => import('./container/Contact'));
const About = React.lazy(() => import('./container/About'));
const Projects = React.lazy(() => import('./container/Projects/Projects'));
const Home = React.lazy(() => import('./container/Home'));

class App extends Component{
  constructor() {
    super();
    this.state = { width: 0, height: 0, desktop: true };   
} 

render() {
  console.log(this.state)

  return (
    <div className="AppStyling">
      <Router>
        <Navbar className="navb" collapseOnSelect expand="lg">
          <Navbar.Brand class="navbar-brand"><Link to="/">Portfolio</Link></Navbar.Brand>
          <Navbar.Toggle aria-controls="responsive-navbar-nav" />
          <Navbar.Collapse id="responsive-navbar-nav">
            <Nav className="ml-auto route">
              <Nav.Link><Link to="/about">About</Link></Nav.Link>
              <Nav.Link><Link to="/projects">Projects</Link></Nav.Link>
              <Nav.Link><Link to="/contact">Contact</Link></Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Navbar>

        <Suspense fallback={<div><Loader /></div>}>
          <Switch>
            <Route exact path="/" component={Home} />
            <Route path="/about" component={About} />
            <Route path="/projects" component={Projects} />
            <Route path="/contact" component={Contact} />
          </Switch>
          <footer class="page-footer">
            <div class="footer-copyright text-center py-3">Karan's Portfolio | 
            <span> Â© 2021</span>
            </div>
          </footer>
        </Suspense>
      </Router>
    </div>
  );
}

}

export default App;
